import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { fetchdata } from "../../Store/actions";
import Posts from "../Posts/posts";
import Search from "../Search/search";
import Paginate from "../Paginate/paginate";
import Cards from "../Cards/cards";

const Mainpage = () => {
  const dispatch = useDispatch();
  const data = useSelector((state) => state.sampleData);
  const [noofPosts, setnoofPosts] = useState(0);
  const [perpage, setperpage] = useState(12);
  const [currentpage, setcurrentpage] = useState(1);
  const [startpos, setstartpos] = useState(0);
  const [endpos, setendpos] = useState(0);
  const [filtereddata, setfiltereddata] = useState([]);
  const [dispalypaginate, setdisplaypaginate] = useState(false);
  const [searchval, setsearchval] = useState("");
  useEffect(() => {
    dispatch(fetchdata());
    setdisplaypaginate(false);
    setendpos(currentpage * perpage);
    setstartpos(endpos - perpage);
  }, [dispatch, startpos, endpos, noofPosts, currentpage]);

  // useEffect(() => {

  // }, []);

  const prevclickHandler = () => {
    setcurrentpage(currentpage - 1);
  };

  const nextclickhandler = () => {
    setcurrentpage(currentpage + 1);
  };

  const changeHandler = (event) => {
    if (event.target.value.length > 0) {
      setdisplaypaginate(true);
    }
    if (event.target.value.length === 0) {
      setdisplaypaginate(false);
    }
    setsearchval(event.target.value);
  };
  const updateddata = data;
  // const changeddata = updateddata.filter((post) =>
  //   post.title.toLowerCase().search(event.target.value.toLowerCase())
  const changeddata = updateddata.filter((post) =>
    post.title.toLowerCase().includes(searchval.toLowerCase())
  );

  // setfiltereddata(changeddata);
  const currentPosts = changeddata.slice(startpos, endpos);

  return (
    <React.Fragment>
      {/* <div className="container"> */}
      <div className="row">
        <Search change={(event) => changeHandler(event)} />
      </div>
      <div className="row">
        {currentPosts.map((post) => (
          <Cards postdata={post} />
        ))}
      </div>
      {/* {dispalypaginate ? ( */}
      <div className="row">
        <Paginate
          prevclick={prevclickHandler}
          nextclick={nextclickhandler}
          curpage={currentpage}
          totalposts={changeddata.length}
          postperpage={perpage}
        />
      </div>
      {/* ) : null} */}
      {/* </div> */}
    </React.Fragment>
  );
};

export default Mainpage;
