import {
  FETCH_POST_DATA,
  FETCH_POST_SUCCESS,
  FETCH_POST_DATA_LOADING,
  FETCH_POST_ERROR,
} from "./actions";

import * as actions from "./actiontypes";

const initialState = {
  ServerPostsData: [],
  loading: false,
  error: false,
};

export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actions.FETCH_POST_DATA_LOADING: {
      return {
        ...state,
        loading: true,
      };
    }
    case actions.FETCH_POST_DATA_ERROR: {
      return {
        ...state,
        error: true,
      };
    }
    case actions.FETCH_POST_DATA:
      return {
        ...state,
        ServerPostsData: action.payload,
      };
    default: {
      return state;
    }
  }
};

export default reducer;
